import { PhantomWalletAdapter } from '@solana/wallet-adapter-phantom';
import { Connection, PublicKey } from '@solana/web3.js';

const connectWallet = async () => {
  if (!window.solana) {
    alert("Solana wallet not found. Please install Sollet or Phantom extension.");
    return;
  }

  try {
    await window.solana.connect();
    const connection = new Connection(network, opts.preflightCommitment);
    const walletAddress = window.solana.publicKey.toString();
    setWalletAddress(walletAddress);
  } catch (err) {
    console.error("Error connecting wallet:", err);
  }
};


function App() {
  const [selectedOption, setSelectedOption] = useState('');

  const options = ['Option 1', 'Option 2', 'Option 3'];

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  // Declare a state variable for input box
  const [inputValue, setInputValue] = useState("");

  // Function to handle the onClick event of first button
  const handleFirstButton = () => {
    console.log('First Button Clicked');
  };

  // Function to handle the onClick event of second button
