import React, { useState } from 'react';
import { ConnectionProvider, WalletProvider } from '@solana/wallet-adapter-react';
import { WalletAdapterNetwork } from '@solana/wallet-adapter-base';
import { PhantomWalletAdapter } from '@solana/wallet-adapter-phantom';
import { Connection, PublicKey } from '@solana/web3.js';

const ConnectWalletButton = () => {
  const network = WalletAdapterNetwork.Devnet;
  const connection = new Connection(network.solanaUrl);
  const publicKey = new PublicKey('Your Solana Public Key');

  const handleConnectWallet = async () => {
    const walletAdapter = new PhantomWalletAdapter();
    const walletConnected = await walletAdapter.connect();

    if (walletConnected) {
      const publicKey = walletAdapter.publicKey || publicKey;
      console.log('Connected to wallet:', publicKey.toBase58());

      // Use connected wallet here

      walletAdapter.disconnect();
    }
  };
}

function App() {
  const [selectedOption, setSelectedOption] = useState('');

  const options = ['Option 1', 'Option 2', 'Option 3'];

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  // Declare a state variable for input box
  const [inputValue, setInputValue] = useState("");

  // Function to handle the onClick event of first button
  const handleFirstButton = () => {
    console.log('First Button Clicked');
  };

  // Function to handle the onClick event of second button
  const handleSecondButton = () => {
    console.log('Second Button Clicked');
  };

  return (
    <div>
      First,{' '}
      <button onClick={handleSecondButton}>
        click here to connect your local phantom wallet
      </button>
      , then,{' '}
      <input 
        type="text" 
        value={inputValue} 
        onChange={ e => setInputValue(e.target.value)}
        placeholder="input any text here"
      />
      , afterward,{' '}
      <button onClick={handleFirstButton}>
        click here to submit
      </button>
      , note this submission, and more alike, 
      are sent to the Solana blockchain, 
      with an associated public address; 
      If such exists, you may,{' '}
      <select 
        value={selectedOption} 
        onChange={handleOptionChange}
      >
        <option hidden value=''>view and select here</option>
        {options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))}
      </select>
      , and here its associated submitted text: {selectedOption}
    </div>
  );
}

export default App;
