// import { ConnectionProvider, WalletProvider } from '@solana/wallet-adapter-react';

import { Connection } from '@solana/web3.js';

     const network = 'https://api.mainnet-beta.solana.com';
     const connection = new Connection(network);


const createAndSignTransaction = async (wallet: Wallet) => {
  const transaction = new Transaction();
  // Add instructions to the transaction

  // Sign the transaction using the user's wallet
  const signedTransaction = await wallet.signTransaction(transaction);

  // Submit the signed transaction to the Solana network
  const signature = await sendAndConfirmTransaction(connection, signedTransaction);

  console.log('Transaction submitted:', signature);
};




function App() {
  const [selectedOption, setSelectedOption] = useState('');

  const options = ['Option 1', 'Option 2', 'Option 3'];

