import React, { useState } from 'react';

function App() {
  const [selectedOption, setSelectedOption] = useState('');

  const handleOptionChange = (event) => {
    setSelectedOption(event.target.value);
  };

  // Declare a state variable for input box
  const [inputValue, setInputValue] = useState("");

  // Function to handle the onClick event of first button
  const handleFirstButton = () => {
    console.log('First Button Clicked');
  };

  // Function to handle the onClick event of second button
  const handleSecondButton = () => {
    console.log('Second Button Clicked');
  };

  return (
    <div>
      First,{' '}
      <button onClick={handleSecondButton}>
        click here to connect your local phantom wallet
      </button>
      , then,{' '}
      <input 
        type="text" 
        value={inputValue} 
        onChange={ e => setInputValue(e.target.value)}
        placeholder="input any text here"
      />
      , afterward,{' '}
      <button onClick={handleFirstButton}>
        click here to submit
      </button>
      , note this submission, and more alike, 
      are sent to the Solana blockchain, 
      with an associated public address; 
      If such exists, you may,{' '}
      <select 
        value={selectedOption} 
        onChange={handleOptionChange}
      >
        <option hidden value=''>view and select here, and here</option>
        <option value="option1">Option 1</option>
        <option value="option2">Option 2</option>
        <option value="option3">Option 3</option>
      </select>
      its associated submitted text: {selectedOption}
    </div>
  );
}

export default App;
